import classes from "./UserView.module.css"
function UserView({user}){
    return (
      <>
        <div className={classes.wrapper} id="wrapper">
          <button className={classes.close} onClick={()=>{
            document.getElementById("wrapper").classList.add(classes.displayNone);
          }}></button>
          <h1 className={classes.mainTitle}>User info</h1>
          <h2 className={classes.subTitle}>Name</h2>
          <p className={classes.info}>{user.name}</p>
          <h2 className={classes.subTitle}>Email</h2>
          <p className={classes.info}>{user.email}</p>
          <h2 className={classes.subTitle}>Date</h2>
          <p className={classes.info}>{user.birthdate}</p>
          <h2 className={classes.subTitle}>Department</h2>
          <p className={classes.info}>{user.department}</p>
        </div>
        <div className={classes.overlay}></div>
      </>
    );
}
export default UserView;
