import classes from "./AddNewUser.module.css";
import { useState } from "react";
function AddNewUserWrapper() {

    const NAME_REQUIRED = "Please enter your name";
    const EMAIL_REQUIRED = "Please enter your email";
    const EMAIL_INVALID = "Please enter a correct email address format";
  const [toggle, setToggle] = useState(true);
  const axios = require("axios");
   const [inputs, setInputs] = useState({});
   const [selectValue, setSelectValue] = useState("A1");

   const handleChange = (event) => {
     const name = event.target.name;
     const value = event.target.value;
     setInputs((values) => ({ ...values, [name]: value }));
   };



   function changeSelectValue(event) {
     setSelectValue(event.target.value);
   }

   const handleSubmit = (event) => {
     event.preventDefault();
     let nameValid = hasValue(inputs.name, NAME_REQUIRED);
     let emailValid = validateEmail(
       inputs.email,
       EMAIL_REQUIRED,
       EMAIL_INVALID
     );
     let dataValid = hasValue(inputs.date, NAME_REQUIRED);

      axios
        .post("http://localhost:8080/users", {
          name: nameValid,
          email: emailValid,
          birthdate: dataValid,
          department: selectValue,
        })
        .then((resp) => {
          console.log(resp.data);
        })
        .catch((error) => {
          console.log(error);
        });
        setToggle(false);
   };



   function hasValue(input, message) {
     if (input.value.trim() === "") {
       return showError(input, message);
     }
     return input;
   }

   function validateEmail(input, requiredMsg, invalidMsg) {
     if (!hasValue(input, requiredMsg)) {
       input;
     }
     const emailRegex =
       /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

     const email = input.value.trim();
     if (!emailRegex.test(email)) {
       return showError(input, invalidMsg);
     }
     return true;
   }
   function showMessage(input, message, type) {
     const msg = input.parentNode.querySelector("small");
     msg.innerText = message;
     input.className = type ? "success" : "error";
     return type;
   }

   function showError(input, message) {
     return showMessage(input, message, false);
   }

  return (
    <>
      {toggle && (
        <>
          <div className={classes.wrapper}>
            <button
              className={classes.close}
              onClick={() => {
                setToggle(!toggle);
              }}
            ></button>
            <h1 className={classes.mainTitle}>User info</h1>
            <form id="addUser" onSubmit={handleSubmit}>
              <h2 className={classes.subTitle}>Name</h2>
              <input
                className={classes.info}
                placeholder="Enter name"
                name="name"
                value={inputs.name || ""}
                onChange={handleChange}
              />
              <h2 className={classes.subTitle}>Email</h2>
              <input
                className={classes.info}
                type="email"
                name="email"
                placeholder="Enter email"
                value={inputs.email || ""}
                onChange={handleChange}
              />
              <h2 className={classes.subTitle}>Date</h2>
              <input
                className={classes.info}
                type="date"
                name="date"
                value={inputs.date || ""}
                onChange={handleChange}
              />
              <h2 className={classes.subTitle}>Department</h2>
              <select
                className={classes.info}
                value={selectValue}
                onChange={changeSelectValue}
              >
                <option value="A1">A1</option>
                <option value="B2">B2</option>
                <option value="C3">C3</option>
              </select>
              <button
                className={classes.cancel}
                onClick={() => {
                  setToggle(!toggle);
                }}
                type="button"
              >
                Cancel
              </button>
              <button className={classes.confirm} type="submit">
                Confirm
              </button>
            </form>
          </div>
          <div className={classes.overlay}></div>
        </>
      )}
    </>
  );
}
export default AddNewUserWrapper;
