import classes from "./UserView.module.css";
function UserView(props) {
    const axios = require("axios");
     const getUserData = async () => {
       try {
         const { data } = await axios.get(
           `http://localhost:8080/users/` + props.user.id
         );
         return data;
       } catch (err) {
         console.log(err.message);
       }
     };
  return (
    <>
      <div className={classes.wrapper}>
        {props.children}
        <h1 className={classes.mainTitle}>User info</h1>
        <h2 className={classes.subTitle}>Name</h2>
        <p className={classes.info}>{getUserData().name}</p>
        <h2 className={classes.subTitle}>Email</h2>
        <p className={classes.info}>{getUserData().email}</p>
        <h2 className={classes.subTitle}>Date</h2>
        <p className={classes.info}>{getUserData().birthdate}</p>
        <h2 className={classes.subTitle}>Department</h2>
        <p className={classes.info}>{getUserData().department}</p>
      </div>
      <div className={classes.overlay}></div>
    </>
  );
}
export default UserView;
