import { useState } from "react";
function UserEditWrapper({ user }) {
  const [toggle, setToggle] = useState(true);
  return (
    <>
      {toggle && (<>
          <div className={classes.wrapper}>
          <button
            className={classes.close}
            onClick={() => {
              setToggle(!toggle);
            }}
          ></button>
        <h1 className={classes.mainTitle}>User info</h1>
        <h2 className={classes.subTitle}>Name</h2>
        <input className={classes.info} placeholder={user.name} />
        <h2 className={classes.subTitle}>Email</h2>
        <input className={classes.info} type={"email"} placeholder={user.email} />
        <h2 className={classes.subTitle}>Date</h2>
        <input className={classes.info} type={"date"} placeholder={user.birthdate} />
        <h2 className={classes.subTitle}>Department</h2>
        <input className={classes.info} placeholder={puser.department} />
      </div>
      <div className={classes.overlay}></div>
    </>


      )}
    </>
  );
}
export default UserEditWrapper;
