import UserSettings from "./UserSettings";
import {useState} from 'react';
import classes from "./Table.module.css";
function UserSettingsButton({user}){
    const [toggle, setToggle] = useState(false);
    document.addEventListener("click", (e) => {
      const withinBoundaries =
        e
          .composedPath()
          .includes(document.getElementById("settingsWrapper" + user.id)) &&
        e
          .composedPath()
          .includes(document.getElementById("buttonSettings" + user.id));
      if (!withinBoundaries) {
        setToggle(false);
      }
    });
    return (
      <>
        <button
          className={classes.dots}
          onClick={() => {
            setToggle(!toggle);
          }}
          id={"buttonSettings" + user.id}
        ></button>
        {toggle && <UserSettings user={user} id={"settingsWrapper" + user.id} />}
      </>
    );
}

export default UserSettingsButton;
