import classes from "./AddNewUser.module.css";
import { useState } from "react";
function AddNewUserWrapper() {
  const [toggle, setToggle] = useState(true);
  const axios = require("axios");
 const AddUser =()=>{
        axios
          .post("http://localhost:8080/users", {
            name: "dasha boyko",
            email: "freddyb34@gmail.com",
            department: "A1",
            birthdate: "2006 31 01"
          })
          .then((resp) => {
            console.log(resp.data);
          })
          .catch((error) => {
            console.log(error);
          });
 }
    function constructor(props){
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  function handleChange(event) {
    this.setState({value: event.target.value});
  }
  return (
    <>
      {toggle && (
        <>
          <div className={classes.wrapper}>
            <button
              className={classes.close}
              onClick={() => {
                setToggle(!toggle);
              }}
            ></button>
            <button
              className={classes.cancel}
              onClick={() => {
                setToggle(!toggle);
              }}
            >
              Cancel
            </button>
            <button
              className={classes.confirm}
              type={"submit"}
              onClick={() => {
                setToggle(!toggle);
                AddUser();
              }}
            >
              Confirm
            </button>
            <h1 className={classes.mainTitle}>User info</h1>
            <h2 className={classes.subTitle}>Name</h2>
            <input
              value={this.state.value}
              onChange={this.handleChange}
              className={classes.info}
              placeholder="Enter name"
              name="name"
            />
            <h2 className={classes.subTitle}>Email</h2>
            <input
              className={classes.info}
              type={"email"}
              placeholder="Enter email"
            />
            <h2 className={classes.subTitle}>Date</h2>
            <input
              className={classes.info}
              type={"date"}
              placeholder="Enter birth date"
            />
            <h2 className={classes.subTitle}>Department</h2>
            <input className={classes.info} placeholder="Enter department" />
          </div>
          <div className={classes.overlay}></div>
        </>
      )}
    </>
  );
}
export default AddNewUserWrapper;
